import React, { useState, useEffect } from 'react';
import './Questions.css';

const Questions = () => {
  const questions = [
    {
      question: 'São tipos de tecido osseo?',
      answers: ['Compacto', 'Esponjoso', 'Femur', 'Carta', 'Braquial'],
      correctAnswers: ['Compacto', 'Esponjoso']
    },
    {
      question: 'Classificação histologica dos ossos',
      answers: ['Femur', 'Carta', 'Braquial'],
      correctAnswers: ['Imaturo', 'Lamelar']
    }
    // Adicione o restante das perguntas...
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showNextButton, setShowNextButton] = useState(false);
  const [wordGrid, setWordGrid] = useState([]);

  useEffect(() => {
    generateWordGrid();
  }, [currentQuestion]);

  const generateWordGrid = () => {
    const grid = [];
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'; // Lista de letras maiúsculas

    for (let i = 0; i < 15; i++) {
      const row = [];
      for (let j = 0; j < 15; j++) {
        // Seleciona aleatoriamente um caractere da lista e insere na célula
        const randomIndex = Math.floor(Math.random() * characters.length);
        const randomChar = characters[randomIndex];
        row.push(randomChar);
      }
      grid.push(row);
    }

    // Preenche a linha de respostas na matriz 15x15
    questions[currentQuestion].answers.forEach((answer) => {
      let placed = false;
      while (!placed) {
        const direction = Math.random() < 0.5 ? 'horizontal' : 'vertical'; // Seleciona aleatoriamente a direção da palavra

        let row, col;
        if (direction === 'horizontal') {
          // Escolhe uma linha aleatória
          row = Math.floor(Math.random() * 15);
          // Escolhe uma coluna aleatória onde a palavra caiba
          col = Math.floor(Math.random() * (15 - answer.length + 1));
        } else {
          // Escolhe uma coluna aleatória
          col = Math.floor(Math.random() * 15);
          // Escolhe uma linha aleatória onde a palavra caiba
          row = Math.floor(Math.random() * (15 - answer.length + 1));
        }

        // Verifica se a palavra cabe na direção escolhida e insere na matriz
        let canPlace = true;
        for (let i = 0; i < answer.length; i++) {
          if (direction === 'horizontal' && grid[row][col + i] !== '') {
            canPlace = false;
            break;
          } else if (direction === 'vertical' && grid[row + i][col] !== '') {
            canPlace = false;
            break;
          }
        }

        if (canPlace) {
          for (let i = 0; i < answer.length; i++) {
            if (direction === 'horizontal') {
              grid[row][col + i] = answer[i];
            } else {
              grid[row + i][col] = answer[i];
            }
          }
          placed = true;
        }
      }
    });

    setWordGrid(grid);
  };

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    setShowNextButton(true);
  };

  const handleNextButtonClick = () => {
    setSelectedAnswer(null);
    setShowNextButton(false);
    setCurrentQuestion(currentQuestion + 1);
  };

  const handleTouchStart = (answer) => {
    setSelectedAnswer(answer);
  };

  const handleTouchEnd = () => {
    setShowNextButton(true);
  };

  return (
    <div className="questions-container">
      <h2 className="question">{questions[currentQuestion].question}</h2>
      <div className="word-grid">
        {wordGrid.map((row, rowIndex) => (
          <div key={rowIndex} className="word-grid-row">
            {row.map((cell, colIndex) => (
              <div key={colIndex} className="word-grid-cell">{cell}</div>
            ))}
          </div>
        ))}
      </div>
      <div className="answers-container">
        {questions[currentQuestion].answers.map((answer, index) => (
          <div
            key={index}
            className={`answer ${selectedAnswer === answer ? 'selected' : ''}`}
            onClick={() => handleAnswerClick(answer)}
            onTouchStart={() => handleTouchStart(answer)}
            onTouchEnd={handleTouchEnd}
          >
            {answer}
          </div>
        ))}
      </div>
      {showNextButton && (
        <button className="next-button" onClick={handleNextButtonClick}>Próxima</button>
      )}
    </div>
  );
};

export default Questions;
