import React, { useState, useEffect } from 'react';
import './Questions.css';

const Questions = () => {
  const questions = [
    {
      question: 'São tipos de tecido ósseo?',
      answers: ['Compacto', 'Esponjoso', 'Femur', 'Carta', 'Braquial'],
      correctAnswers: ['COMPACTO', 'Esponjoso']
    },
    {
      question: 'Classificação histológica dos ossos',
      answers: ['Femur', 'Carta', 'Braquial'],
      correctAnswers: ['Imaturo', 'Lamelar']
    }
    // Adicione o restante das perguntas...
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showNextButton, setShowNextButton] = useState(false);
  const [wordGrid, setWordGrid] = useState([]);

  useEffect(() => {
    generateWordGrid();
  }, [currentQuestion]);

  const generateWordGrid = () => {
    const grid = Array.from({ length: 8 }, () => Array(8).fill(''));

    const availableChars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';

    questions[currentQuestion].correctAnswers.forEach((word) => {
      const wordLength = word.length;
      let row, col;
      let tries = 0;
      let direction;

      // Seleciona uma direção aleatória: horizontal (0) ou vertical (1)
      direction = Math.random() < 0.5 ? 0 : 1;

      do {
        row = Math.floor(Math.random() * 8);
        col = Math.floor(Math.random() * 8);
        tries++;
        if (tries > 100) return; // Evita loop infinito
      } while (!canPlaceWord(grid, row, col, wordLength, direction));

      placeWord(grid, row, col, word, direction);
    });

    setWordGrid(grid);
  };

  const canPlaceWord = (grid, row, col, length, direction) => {
    if (direction === 0 && col + length > 8) return false; // Fora dos limites na horizontal
    if (direction === 1 && row + length > 8) return false; // Fora dos limites na vertical

    for (let i = 0; i < length; i++) {
      if (direction === 0 && grid[row][col + i] !== '') return false; // Colisão na horizontal
      if (direction === 1 && grid[row + i][col] !== '') return false; // Colisão na vertical
    }

    return true;
  };

  const placeWord = (grid, row, col, word, direction) => {
    if (direction === 0) {
      for (let i = 0; i < word.length; i++) {
        grid[row][col + i] = word[i];
      }
    } else {
      for (let i = 0; i < word.length; i++) {
        grid[row + i][col] = word[i];
      }
    }
  };

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    setShowNextButton(true);
  };

  const handleNextButtonClick = () => {
    setSelectedAnswer(null);
    setShowNextButton(false);
    setCurrentQuestion(currentQuestion + 1);
  };

  const handleTouchStart = (answer) => {
    setSelectedAnswer(answer);
  };

  const handleTouchEnd = () => {
    setShowNextButton(true);
  };

  return (
    <div className="questions-container">
      <div className="question-background">
        <h2 className="question">{questions[currentQuestion].question}</h2>
      </div>
      <div className="grid-container">
        <div className="word-grid">
          {wordGrid.map((row, rowIndex) => (
            <div key={rowIndex} className="word-grid-row">
              {row.map((cell, colIndex) => (
                <div key={colIndex} className="word-grid-cell">{cell}</div>
              ))}
            </div>
          ))}
        </div>
      </div>
      <div className="answers-container">
        {questions[currentQuestion].correctAnswers.map((answer, index) => (
          <div
            key={index}
            className={`answer ${selectedAnswer === answer ? 'selected' : ''}`}
            onClick={() => handleAnswerClick(answer)}
            onTouchStart={() => handleTouchStart(answer)}
            onTouchEnd={handleTouchEnd}
          >
            {answer}
          </div>
        ))}
      </div>
      {showNextButton && (
        <button className="next-button" onClick={handleNextButtonClick}>Próxima</button>
      )}
    </div>
  );
};

export default Questions;
