import React, { useState, useEffect } from 'react';
import './Questions.css';

const Questions = () => {
  const questions = [
    {
      question: 'São tipos de tecido ósseo?',
      correctAnswers: ['COMPACTO', 'ESPONJOSO']
    },
    {
      question: 'Classificação histológica dos ossos',
      correctAnswers: ['IMATURO', 'LAMELAR']
    }
    // Add the rest of the questions...
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswers, setSelectedAnswers] = useState([]);
  const [wordGrid, setWordGrid] = useState([]);
  const gridSize = wordGrid.length || 8; // Set gridSize to the actual grid length or 8 if it's not defined yet

  useEffect(() => {
    generateWordGrid();
    setSelectedAnswers(Array(gridSize * gridSize).fill(''));
  }, [currentQuestion]);

  const generateWordGrid = () => {
    const longestWordLength = Math.max(...questions[currentQuestion].correctAnswers.map(word => word.length));
    const gridSize = Math.max(8, longestWordLength);
    const grid = Array.from({ length: gridSize }, () => Array(gridSize).fill(''));

    const availableChars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';

    questions[currentQuestion].correctAnswers.forEach((word) => {
      let row, col;
      let tries = 0;
      let direction;

      // Select a random direction: horizontal (0) or vertical (1)
      direction = Math.random() < 0.5 ? 0 : 1;

      do {
        row = Math.floor(Math.random() * gridSize);
        col = Math.floor(Math.random() * gridSize);
        tries++;
        if (tries > 100) return; // Avoid infinite loop
      } while (!canPlaceWord(grid, row, col, word.length, direction));

      placeWord(grid, row, col, word.length, direction);
    });

    setWordGrid(grid);
  };

  const canPlaceWord = (grid, row, col, wordLength, direction) => {
    for (let i = 0; i < wordLength; i++) {
      const r = direction === 0 ? row : row + i;
      const c = direction === 0 ? col + i : col;
      if (grid[r][c] !== '') return false;
    }
    return true;
  };

  const placeWord = (grid, row, col, wordLength, direction) => {
    for (let i = 0; i < wordLength; i++) {
      const r = direction === 0 ? row : row + i;
      const c = direction === 0 ? col + i : col;
      grid[r][c] = questions[currentQuestion].correctAnswers[i];
    }
  };

  const handleSubmit = () => {
    // Validate selected answers...
  };

  const handleSelect = (row, col) => {
    // Handle answer selection...
  };

  const renderWordGrid = () => {
    return wordGrid.map((row, i) => (
      <div key={i} className="grid-row">
        {row.map((cell, j) => (
          <div
            key={j}
            className={`grid-cell ${selectedAnswers[i * gridSize + j] ? 'selected' : ''}`}
            onClick={() => handleSelect(i, j)}
          >
            {cell || ' '}
          </div>
        ))}
      </div>
    ));
  };

  return (
    <div className="Questions">
      <div className="word-grid">{renderWordGrid()}</div>
      <button onClick={handleSubmit}>Enviar</button>
    </div>
  );
};

export default Questions;
