import React, { useState, useEffect } from 'react';
import './Questions.css';

const Questions = () => {
  const questions = [
    {
      question: 'São tipos de tecido osseo?',
      answers: ['Compacto', 'Esponjoso', 'Femur', 'Carta', 'Braquial'],
      correctAnswers: ['Compacto', 'Esponjoso']
    },
    {
      question: 'Classificação histologica dos ossos',
      answers: ['Femur', 'Carta', 'Braquial'],
      correctAnswers: ['Imaturo', 'Lamelar']
    }
    // Adicione o restante das perguntas...
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showNextButton, setShowNextButton] = useState(false);
  const [wordGrid, setWordGrid] = useState([]);

  useEffect(() => {
    generateWordGrid();
  }, [currentQuestion]);

  const generateWordGrid = () => {
    const grid = Array.from({ length: 15 }, () => Array(15).fill(''));

    const availableChars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';

    questions[currentQuestion].answers.forEach((word) => {
      const wordLength = word.length;
      let row, col;
      let tries = 0;

      do {
        row = Math.floor(Math.random() * 15);
        col = Math.floor(Math.random() * 15);
        tries++;
        if (tries > 100) return; // Evita loop infinito
      } while (!canPlaceWord(grid, row, col, wordLength));

      placeWord(grid, row, col, word);
    });

    // Preenche os espaços vazios com caracteres aleatórios
    for (let row = 0; row < 15; row++) {
      for (let col = 0; col < 15; col++) {
        if (!grid[row][col]) {
          grid[row][col] = availableChars[Math.floor(Math.random() * availableChars.length)];
        }
      }
    }

    setWordGrid(grid);
  };

  const canPlaceWord = (grid, row, col, length) => {
    if (row < 0 || col < 0 || row + length > 15 || col + length > 15) return false; // Fora dos limites

    const directions = [[1, 0], [0, 1], [1, 1], [1, -1]];
    for (const [dx, dy] of directions) {
      let isValid = true;
      let r = row, c = col;
      for (let i = 0; i < length; i++) {
        if (!grid[r] || grid[r][c] !== '') {
          isValid = false;
          break;
        }
        r += dx;
        c += dy;
      }
      if (isValid) return true;
    }
    return false;
  };

  const placeWord = (grid, row, col, word) => {
    const directions = [[1, 0], [0, 1], [1, 1], [1, -1]];
    const [dx, dy] = directions[Math.floor(Math.random() * directions.length)];
    let r = row, c = col;
    for (const char of word) {
      grid[r][c] = char;
      r += dx;
      c += dy;
    }
  };

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    setShowNextButton(true);
  };

  const handleNextButtonClick = () => {
    setSelectedAnswer(null);
    setShowNextButton(false);
    setCurrentQuestion(currentQuestion + 1);
  };

  const handleTouchStart = (answer) => {
    setSelectedAnswer(answer);
  };

  const handleTouchEnd = () => {
    setShowNextButton(true);
  };

  return (
    <div className="questions-container">
      <h2 className="question">{questions[currentQuestion].question}</h2>
      <div className="word-grid">
        {wordGrid.map((row, rowIndex) => (
          <div key={rowIndex} className="word-grid-row">
            {row.map((cell, colIndex) => (
              <div key={colIndex} className="word-grid-cell">{cell}</div>
            ))}
          </div>
        ))}
      </div>
      <div className="answers-container">
        {questions[currentQuestion].answers.map((answer, index) => (
          <div
            key={index}
            className={`answer ${selectedAnswer === answer ? 'selected' : ''}`}
            onClick={() => handleAnswerClick(answer)}
            onTouchStart={() => handleTouchStart(answer)}
            onTouchEnd={handleTouchEnd}
          >
            {answer}
          </div>
        ))}
      </div>
      {showNextButton && (
        <button className="next-button" onClick={handleNextButtonClick}>Próxima</button>
      )}
    </div>
  );
};

export default Questions;
