import React, { useState, useEffect } from 'react';
import axios from 'axios';
import wordSearchGenerator from './wordSearchGenerator';
import './Questions.css';

const Questions = () => {
  const questions = [
    {
      question: 'São tipos de tecido ósseo?',
      correctAnswers: ['COMPACTO', 'ESPONJOSO']
    },
    {
      question: 'Classificação histológica dos ossos',
      correctAnswers: ['IMATURO', 'LAMELAR']
    }
    // Add the rest of the questions...
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [wordGrid, setWordGrid] = useState([]);
  const [selectedAnswers, setSelectedAnswers] = useState([]);
  const [isNextButtonDisabled, setIsNextButtonDisabled] = useState(true);

  useEffect(() => {
    fetchQuestions();
  }, []);

  useEffect(() => {
    if (questions.length > 0) {
      generateWordGrid();
    }
  }, [currentQuestion, questions]);

  const fetchQuestions = async () => {
    try {
      const response = await axios.get('https://wordsearchapi.com/api/v1/random');
      setQuestions(response.data.map(question => ({
        ...question,
        correctAnswers: question.answer.split(',')
      })));
    } catch (error) {
      console.error('Error fetching questions:', error);
    }
  };

  const generateWordGrid = () => {
    const grid = wordSearchGenerator.generateGrid(questions[currentQuestion].correctAnswers);
    setWordGrid(grid);
    setSelectedAnswers(Array(grid.length * grid[0].length).fill(''));
    setIsNextButtonDisabled(true);
  };

  const handleAnswerClick = (row, col) => {
    const newSelectedAnswers = [...selectedAnswers];
    newSelectedAnswers[row * wordGrid.length + col] = wordGrid[row][col];
    setSelectedAnswers(newSelectedAnswers);

    const selectedWord = newSelectedAnswers.join('');
    const isCorrect = questions[currentQuestion].correctAnswers.some(word => word === selectedWord);
    setIsNextButtonDisabled(!isCorrect);
  };

  const handleNextQuestion = () => {
    setCurrentQuestion(currentQuestion + 1);
  };

  if (questions.length === 0) {
    return <div>Loading...</div>;
  }

  const question = questions[currentQuestion];
  return (
    <div className="questions-container">
      <div className="question-background">
        <h2 className="question">{question.question}</h2>
      </div>
      <div className="grid-container">
        <div className="word-grid">
          {wordGrid.map((row, rowIndex) => (
            <div key={rowIndex} className="word-grid-row">
              {row.map((cell, colIndex) => (
                <div
                  key={colIndex}
                  className={`word-grid-cell ${cell ? 'populated' : ''} ${selectedAnswers[rowIndex * wordGrid.length + colIndex] ? 'selected' : ''}`}
                  onClick={() => handleAnswerClick(rowIndex, colIndex)}
                >
                  {cell}
                </div>
              ))}
            </div>
          ))}
        </div>
      </div>
      <button
        className="next-button"
        onClick={handleNextQuestion}
        disabled={isNextButtonDisabled}
      >
        Próxima questão
      </button>
    </div>
  );
};

export default Questions;
