import React, { useState, useEffect } from 'react';
import './Questions.css';

const Questions = () => {
  // Definindo as perguntas e respostas
  const questions = [
    {
      question: '1. Quais são os tipos de tecido ósseo?',
      answer: 'compacto, esponjoso',
    },
    {
      question: '2. Qual é a classificação histológica dos ossos?',
      answer: 'Imaturo, lamelar',
    },
    {
      question: '3. Quais são os tipos de ossificação?',
      answer: 'Intramembranosa, endocondral',
    },
    {
      question: '4. Quais são as duas camadas principais dos ossos?',
      answer: 'Endósteo, periósteo',
    },
    {
      question: '5. Qual é a célula responsável pela produção de matriz óssea?',
      answer: 'Osteoblasto',
    },
    {
      question: '6. Quais são os principais minerais presentes nos ossos?',
      answer: 'Cálcio e fósforo',
    },
  ];

  // Estado para controlar a pergunta atual
  const [currentQuestion, setCurrentQuestion] = useState(0);

  // Estado para armazenar o dashboard de palavras para cada pergunta
  const [wordGrids, setWordGrids] = useState([]);

  // Função para gerar um dashboard de palavras 15x15
  const generateWordGrid = () => {
    const grid = [];
    for (let i = 0; i < 15; i++) {
      const row = [];
      for (let j = 0; j < 15; j++) {
        // Adicione aqui a lógica para preencher o grid com caracteres aleatórios
        // Por enquanto, vamos preencher com um caractere aleatório
        row.push(String.fromCharCode(65 + Math.floor(Math.random() * 26))); // Caracteres aleatórios de A a Z
      }
      grid.push(row);
    }
    return grid;
  };

  // Função para avançar para a próxima pergunta
  const nextQuestion = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      // Aqui você pode adicionar lógica para lidar com o término das perguntas, se necessário
      console.log('Fim das perguntas');
    }
  };

  // Efeito para gerar o dashboard de palavras para cada pergunta ao carregar o componente
  useEffect(() => {
    const grids = questions.map(() => generateWordGrid());
    setWordGrids(grids);
  }, []);

  return (
    <div className="questions-container">
      <div className="question">{questions[currentQuestion].question}</div>
      {/* Aqui você pode renderizar o dashboard de palavras */}
      <div className="word-grid">
        {wordGrids[currentQuestion] &&
          wordGrids[currentQuestion].map((row, rowIndex) => (
            <div key={rowIndex} className="word-grid-row">
              {row.map((cell, cellIndex) => (
                <div key={cellIndex} className="word-grid-cell">{cell}</div>
              ))}
            </div>
          ))}
      </div>
      <button className="next-button" onClick={nextQuestion}>Próxima</button>
    </div>
  );
};

export default Questions;
